import React, { useState, useEffect, useCallback, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@mui/material";
import Loadable from 'react-loadable';
import moment from 'moment';
import AdNavBar from "../../components/AdNavBar";
import "./AdHome.css";

const AdComment = Loadable({
  loader: () => import('./AdComment'),
  loading: () => <div>Loading...</div>,
});

const AdHome = () => {
  const navigate = useNavigate();
  const [isOverlayVisible, setOverlayVisible] = useState(false);
  const [newPostContent, setNewPostContent] = useState("");
  const [posts, setPosts] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [currentPostId, setCurrentPostId] = useState(null);
  const [currentPostOwner, setCurrentPostOwner] = useState(null);
  const [comments, setComments] = useState([]);
  const [isCommentDialogOpen, setIsCommentDialogOpen] = useState(false);
  const [loggedInAdmin, setLoggedInAdmin] = useState(null);
  const [loggedInSuperUser, setLoggedInSuperUser] = useState(null); // To hold Super User data
  const [newComment, setNewComment] = useState('');
  const [isDeletePostDialogOpen, setIsDeletePostDialogOpen] = useState(false);
  const [isDeleteCommentDialogOpen, setIsDeleteCommentDialogOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [profilePicture, setProfilePicture] = useState(null); // State to hold logged-in admin's profile picture
  const [adminProfilePictures, setAdminProfilePictures] = useState({}); // State to hold profile pictures for posts
  const defaultProfile = '/dp.png'; // Path to the default profile picture
  const [inputHasContent, setInputHasContent] = useState(false);
  const [showCancelButton, setShowCancelButton] = useState(false);
  const [showCloseButton, setShowCloseButton] = useState(false);

  const fileInputRef = useRef(null);

  // Fetch the logged-in admin data
  useEffect(() => {
    const fetchLoggedInAdmin = async () => {
      const storedAdmin = JSON.parse(localStorage.getItem("loggedInAdmin"));
      if (storedAdmin && storedAdmin.adminname) {
        try {
          const response = await axios.get(`http://localhost:8080/admin/getByAdminname?adminname=${storedAdmin.adminname}`);
          const adminData = response.data;
          if (adminData && adminData.adminId) {
            setLoggedInAdmin(adminData);
            fetchLoggedInAdminProfilePicture(adminData.adminId);
          } else {
            console.error("Admin data is missing an admin ID.");
          }
        } catch (error) {
          console.error("Error fetching admin data:", error);
        }
      }
    };
    fetchLoggedInAdmin();
  }, []);

  // Fetch profile picture for logged-in admin
  const fetchLoggedInAdminProfilePicture = async (adminId) => {
    try {
      const response = await fetch(`http://localhost:8080/admin/profile/getProfilePicture/${adminId}`);
      if (response.ok) {
        const imageBlob = await response.blob();
        const imageUrl = URL.createObjectURL(imageBlob);
        setProfilePicture(imageUrl);
      } else {
        setProfilePicture(defaultProfile);
      }
    } catch (error) {
      console.error('Failed to fetch admin profile picture:', error);
      setProfilePicture(defaultProfile);
    }
  };

  // Fetch profile picture for admins in posts
  const fetchAdminProfilePictureForPosts = async (adminId) => {
    try {
      const response = await fetch(`http://localhost:8080/admin/profile/getProfilePicture/${adminId}`);
      if (response.ok) {
        const imageBlob = await response.blob();
        const imageUrl = URL.createObjectURL(imageBlob);
        setAdminProfilePictures(prev => ({ ...prev, [adminId]: imageUrl }));
      } else {
        setAdminProfilePictures(prev => ({ ...prev, [adminId]: defaultProfile }));
      }
    } catch (error) {
      console.error('Failed to fetch admin profile picture for posts:', error);
      setAdminProfilePictures(prev => ({ ...prev, [adminId]: defaultProfile }));
    }
  };

  // Fetch posts and profile pictures for posts
  useEffect(() => {
    const fetchPostsAndPictures = async () => {
      try {
        const response = await axios.get("http://localhost:8080/posts");
        const sortedPosts = response.data.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
        setPosts(sortedPosts);

        // Fetch profile pictures for each admin in posts
        const adminIds = new Set(sortedPosts.map(post => post.adminId));
        adminIds.forEach(adminId => fetchAdminProfilePictureForPosts(adminId));
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPostsAndPictures();
  }, []);

  const handlePostInputChange = (e) => {
    const content = e.target.value;
    setNewPostContent(content);
    setShowCloseButton(content.length > 0 || imagePreview !== null);
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
        setShowCloseButton(true);
      };
      reader.readAsDataURL(file);
    }
  };

  const handlePostSubmit = async (e) => {
    e.preventDefault();
    if (!newPostContent && !imagePreview) {
      alert("Please enter a post or select a picture before submitting.");
      return;
    }
    if (!loggedInAdmin) {
      alert("Please log in to post.");
      return;
    }

    const newPost = {
      content: newPostContent,
      image: imagePreview,
      adminId: loggedInAdmin.adminId,
      fullName: loggedInAdmin.fullName,
      idNumber: loggedInAdmin.idNumber,
      isVerified: false,
      likes: 0,
      dislikes: 0,
    };

    try {
      const response = await axios.post("http://localhost:8080/posts/add", newPost, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      setPosts(prevPosts => [response.data, ...prevPosts]);
      setNewPostContent("");
      setSelectedFile(null);
      setImagePreview(null);
      setShowCloseButton(false);
    } catch (error) {
      console.error("Error posting data:", error);
      if (error.response) {
        console.error("Response data:", error.response.data);
        console.error("Response status:", error.response.status);
      }
    }
  };

  return (
    <div className="adhome">
      <AdNavBar />
      <b className="HWildcat">WILDCAT</b>

      <div className="content-wrapper">
        <div className="post-container">
          <div className="logo-container">
            <img src={profilePicture || defaultProfile} alt="Admin Avatar" className="admins-dp" />
          </div>
          <div className="post-form">
            <form onSubmit={handlePostSubmit}>
              <div className="input-container">
                <input
                  type="text"
                  className="post-input"
                  value={newPostContent}
                  onChange={handlePostInputChange}
                  placeholder="What's happening in your day, Wildcat?"
                />
                {showCloseButton && (
                  <button type="button" className="close-button" onClick={() => setNewPostContent('')}>
                    Ã—
                  </button>
                )}
              </div>
              <div className="post-subcontainer">
                <div className="post-subcontainer-icons">
                  <label htmlFor="file-upload">
                    <img className="gallery-icon" alt="" src="/gallery.png" />
                  </label>
                  <input
                    ref={fileInputRef}
                    id="file-upload"
                    type="file"
                    className="file-input"
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                    onClick={(e) => { e.target.value = null }}
                  />
                  <img className="mic-icon" alt="Mic" src="/mic.png" />
                </div>
                <Button
                  type="submit"
                  className="post-button"
                  variant="contained"
                  sx={{
                    borderRadius: "10px",
                    width: 60,
                    height: 30,
                    backgroundColor: "#8A252C",
                    "&:hover": { backgroundColor: "#A91D3A" }
                  }}
                >
                  POST
                </Button>
              </div>
            </form>
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" style={{ width: '100px', height: '100px' }} />
              </div>
            )}
          </div>
        </div>

        <div className="post-list">
          {posts.map((post) => (
            <div key={post.postId} className="post-card">
              <div className="card-container">
                <div className="name-container">
                  <img src={adminProfilePictures[post.adminId] || defaultProfile} alt="Admin Avatar" />
                  <h5>{post.fullName} ({post.idNumber})</h5>
                </div>
                <div className="timestamp">
                  <span className="formatted-date">{moment(post.timestamp).format('dddd, MMMM D, YYYY [at] h:mm A')}</span>
                  <br />
                  <span className="relative-time">{moment(post.timestamp).fromNow()}</span>
                </div>
                <div className="card-contents">
                  <p>{post.content}</p>
                  {post.image && (
                    <img className="post-image" alt="Post" src={post.image} style={{ maxWidth: '100%', height: 'auto' }} />
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdHome;
